// Função para traduzir texto
function t(key, ...args) {
  let text = translations[currentLanguage][key] || key;
  
  // Substituir placeholders {0}, {1}, etc.
  if (args.length > 0) {
    args.forEach((arg, index) => {
      text = text.replace(`{${index}}`, arg);
    });
  }
  
  return text;
}

// Mudar idioma da interface
function changeLanguage(lang) {
  if (translations[lang]) {
    currentLanguage = lang;
    localStorage.setItem('language', lang);
    updateUILanguage();
  }
}

// Atualizar idioma da interface
function updateUILanguage() {
  // Atualizar textos da navegação
  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    if (key) {
      el.textContent = t(key);
    }
  });
  
  // Atualizar placeholders
  document.querySelectorAll('[data-i18n-placeholder]').forEach(el => {
    const key = el.getAttribute('data-i18n-placeholder');
    if (key) {
      el.placeholder = t(key);
    }
  });
  
  // Atualizar botões
  document.querySelectorAll('button[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    if (key) {
      el.textContent = t(key);
    }
  });
  
  // Atualizar card atual se estiver na página principal
  if (document.getElementById('english')) {
    updateCardDisplay();
  }
}

// Inicializar tema
function initTheme() {
  const savedTheme = localStorage.getItem('theme') || 'auto';
  setTheme(savedTheme);
}

// Definir tema
function setTheme(theme) {
  localStorage.setItem('theme', theme);
  
  if (theme === 'auto') {
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    document.documentElement.setAttribute('data-bs-theme', prefersDark ? 'dark' : 'light');
  } else {
    document.documentElement.setAttribute('data-bs-theme', theme);
  }
}

// Carregar flashcards do banco de dados
async function loadFlashcards() {
  try {
    if (currentCategory === 'all') {
      flashcards = await getAllFlashcards();
    } else if (currentCategory === 'favorites') {
      flashcards = await db.getAllFavorites();
      if (flashcards.length === 0) {
        alert(t('no_favorites'));
        currentCategory = 'all';
        return loadFlashcards();
      }
    } else {
      flashcards = await db.getFlashcardsByCategory(currentCategory);
    }
    
    currentCardIndex = 0;
    updateCardDisplay();
  } catch (error) {
    console.error('Erro ao carregar flashcards:', error);
  }
}

// Mostrar card atual
function updateCardDisplay() {
  if (!flashcards.length) return;
  
  const card = flashcards[currentCardIndex];
  const englishElement = document.getElementById('english');
  const portugueseElement = document.getElementById('portuguese');
  const counterElement = document.getElementById('counter');
  
  if (englishElement) englishElement.innerHTML = card.en;
  if (portugueseElement) {
    portugueseElement.innerHTML = card.pt;
    portugueseElement.style.display = 'none';
  }
  
  if (counterElement) {
    counterElement.textContent = t('card_of', currentCardIndex + 1, flashcards.length);
  }
  
  updateFavoriteIcon();
  updateProgressDisplay();
}

// Alternar exibição da tradução
function toggleTranslation() {
  const trans = document.getElementById('portuguese');
  if (trans) {
    trans.style.display = trans.style.display === 'none' ? 'block' : 'none';
    
    // Atualizar progresso quando a tradução é mostrada
    if (trans.style.display === 'block' && flashcards[currentCardIndex]) {
      db.updateProgress(flashcards[currentCardIndex].id, 'review');
      updateProgressDisplay();
    }
  }
}

// Ir para o próximo card
function nextCard() {
  if (!flashcards.length) return;
  
  currentCardIndex = (currentCardIndex + 1) % flashcards.length;
  updateCardDisplay();
}

// ======= FUNÇÃO: VOLTAR AO CARD ANTERIOR =======
function previousCard() {
  currentCardIndex = (currentCardIndex - 1) % flashcards.length;
  updateCardDisplay();
}

// ======= FUNÇÃO: CARD ALEATÓRIO =======
function randomCard() {
  let randomIndex;
  do {
    randomIndex = Math.floor(Math.random() * flashcards.length);
  } while (randomIndex === currentCardIndex);
  currentCardIndex = randomIndex;
  updateCardDisplay();
}

// ======= FUNÇÃO: TTS - REPRODUZIR FRASE EM INGLÊS =======
function playAudio() {
  const text = document.getElementById("english").textContent;
  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = "en-US";
  speechSynthesis.speak(utterance);
}

let speechRate = 1.0;

function playAudio() {
  const text = document.getElementById("english").textContent;
  const utterance = new SpeechSynthesisUtterance(text);
  utterance.lang = "en-US";
  utterance.rate = speechRate;
  speechSynthesis.speak(utterance);
}

function increaseRate() {
  if (speechRate < 2.0) {
    speechRate += 0.1;
    speechRate = Math.round(speechRate * 10) / 10;
    updateRateDisplay();
  }
}

function decreaseRate() {
  if (speechRate > 0.5) {
    speechRate -= 0.1;
    speechRate = Math.round(speechRate * 10) / 10;
    updateRateDisplay();
  }
}

function updateRateDisplay() {
  document.getElementById("rateDisplay").textContent = speechRate.toFixed(1) + "x";
}


// Alternar favorito
async function toggleFavorite() {
  if (!flashcards.length) return;
  
  const cardId = flashcards[currentCardIndex].id;
  const isFav = await db.isFavorite(cardId);
  
  if (isFav) {
    await db.removeFromFavorites(cardId);
  } else {
    await db.addToFavorites(cardId);
  }
  
  updateFavoriteIcon();
  
  // Mostrar feedback visual
  const icon = document.getElementById('favoriteIcon');
  if (icon) {
    icon.classList.add('animate__animated', 'animate__heartBeat');
    setTimeout(() => {
      icon.classList.remove('animate__animated', 'animate__heartBeat');
    }, 1000);
  }
}

// Atualizar ícone de favorito
async function updateFavoriteIcon() {
  const icon = document.getElementById('favoriteIcon');
  if (!icon || !flashcards.length) return;
  
  const cardId = flashcards[currentCardIndex].id;
  const isFav = await db.isFavorite(cardId);
  
  icon.innerHTML = isFav ? '★' : '☆';
  icon.classList.toggle('active', isFav);
  icon.setAttribute('aria-label', isFav ? 'Remover dos favoritos' : 'Adicionar aos favoritos');
}

// Atualizar exibição de progresso
async function updateProgressDisplay() {
  const progressElement = document.getElementById('progressIndicator');
  if (!progressElement || !flashcards.length) return;
  
  try {
    const cardId = flashcards[currentCardIndex].id;
    const transaction = db.db.transaction(['progress'], 'readonly');
    const store = transaction.objectStore('progress');
    const request = store.get(cardId);
    
    request.onsuccess = (event) => {
      const progress = event.target.result;
      let status = 'new';
      let reviewCount = 0;
      
      if (progress) {
        status = progress.status;
        reviewCount = progress.reviewCount;
      }
      
      // Atualizar indicador visual
      progressElement.className = 'progress-indicator';
      progressElement.classList.add(status);
      
      // Atualizar texto de progresso
      let statusText = '';
      switch (status) {
        case 'new': statusText = 'Novo'; break;
        case 'learning': statusText = 'Aprendendo'; break;
        case 'review': statusText = 'Revisão'; break;
        case 'known': statusText = 'Conhecido'; break;
      }
      
      progressElement.setAttribute('data-reviews', reviewCount);
      progressElement.setAttribute('aria-label', `Status: ${statusText}, Revisões: ${reviewCount}`);
    };
  } catch (error) {
    console.error('Erro ao atualizar progresso:', error);
  }
}

// Salvar novo card
async function saveNewCard(event) {
  if (event) event.preventDefault();
  
  const enText = document.getElementById('enText').value.trim();
  const ptText = document.getElementById('ptText').value.trim();
  const categorySelect = document.getElementById('categorySelect');
  const category = categorySelect ? categorySelect.value : 'geral';
  
  if (enText && ptText) {
    try {
      await db.addFlashcard({ en: enText, pt: ptText, category });
      
      // Limpar formulário
      document.getElementById('enText').value = '';
      document.getElementById('ptText').value = '';
      
      // Mostrar feedback
      showToast(t('save_success'));
    } catch (error) {
      console.error('Erro ao salvar card:', error);
      showToast('Erro ao salvar card.');
    }
  } else {
    showToast(t('fill_fields'));
  }
}

// Exportar dados
async function exportData() {
  try {
    const data = await db.exportAllData();
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    
    const link = document.createElement('a');
    link.href = url;
    link.download = 'flashcards_data.json';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    
    showToast('Dados exportados com sucesso!');
  } catch (error) {
    console.error('Erro ao exportar dados:', error);
    showToast('Erro ao exportar dados.');
  }
}

// Importar dados
async function importData(event) {
  const file = event.target.files[0];
  if (!file) return;
  
  const reader = new FileReader();
  reader.onload = async function(e) {
    try {
      const data = JSON.parse(e.target.result);
      await db.importData(data);
      
      showToast(t('import_success'));
      
      // Recarregar flashcards
      await loadFlashcards();
    } catch (err) {
      console.error('Erro ao importar dados:', err);
      showToast(t('import_error'));
    }
  };
  reader.readAsText(file);
}

