// Verificar respostas do quiz
function checkQuizAnswers() {
  const quizContainer = document.getElementById('quizContainer');
  if (!quizContainer || !quizContainer.dataset.questions) return;
  
  const questions = JSON.parse(quizContainer.dataset.questions);
  let correctCount = 0;
  
  // Verificar cada questão
  questions.forEach((q, index) => {
    const questionElement = document.querySelector(`.quiz-question[data-question-id="${q.id}"]`);
    if (!questionElement) return;
    
    const selectedOption = document.querySelector(`input[name="question${index}"]:checked`);
    const correctOption = q.correctAnswer;
    
    if (selectedOption) {
      const userAnswer = parseInt(selectedOption.value);
      const isCorrect = userAnswer === correctOption;
      
      if (isCorrect) {
        correctCount++;
        questionElement.classList.add('border-success');
      } else {
        questionElement.classList.add('border-danger');
        
        // Destacar resposta correta
        const correctOptionElement = document.getElementById(`q${index}opt${correctOption}`);
        if (correctOptionElement) {
          correctOptionElement.parentElement.classList.add('text-success', 'fw-bold');
        }
      }
      
      // Desabilitar inputs após verificação
      questionElement.querySelectorAll('input').forEach(input => {
        input.disabled = true;
      });
    }
  });
  
  // Mostrar resultado
  const resultHTML = `
    <div class="alert ${correctCount >= questions.length / 2 ? 'alert-success' : 'alert-warning'} mt-3">
      Você acertou ${correctCount} de ${questions.length} questões.
    </div>
  `;
  
  quizContainer.insertAdjacentHTML('beforeend', resultHTML);
  
  // Desabilitar botão de verificar
  const checkButton = quizContainer.querySelector('button');
  if (checkButton) {
    checkButton.disabled = true;
  }
  
  // Adicionar botão para tentar novamente
  quizContainer.insertAdjacentHTML('beforeend', `
    <button class="btn btn-secondary mt-3" onclick="initQuiz()">Tentar Novamente</button>
  `);
}

